export const error = {
  color: 'red',
  'margin-left': '5px',
  'margin-right': '5px',
};

export const errorBorder = {
  border: '1px solid #f00',
  width: '100%',
  'margin-left': '5px',
  'margin-right': '5px',
  padding: '12px 20px',
  display: 'inline-block',
  'border-radius': '4px',
  'box-sizing': 'border-box',
};

export const container = {
  width: '100%',
  display: 'flex',
  'flex-direction': 'column',
  border: '1px solid',
};

export const inputContainer = {
  width: '98%',
  display: 'flex',
  'justify-content': 'center',
  'flex-direction': 'column',
  border: '1px solid',
  'margin-left': '5px',
  'margin-top': '5px',
  'padding-right': '10px',
};

export const input = {
  width: '100%',
  'margin-left': '5px',
  'margin-right': '5px',
  padding: '12px 20px',
  display: 'inline-block',
  border: '1px solid #ccc',
  'border-radius': '4px',
  'box-sizing': 'border-box',
};

export const headingStyle = {
  'margin-left': '5px',
};
